package application;

// Cette classe permet de me stocker 2 points, permettant ainsi de créer une ligne facilement
public class Vector4 {

		public double x;
		public double y;
		public double a;
		public double b;
		
		public Vector4(){
			x = 0;
			y = 0;
			a = 0;
			b = 0;
		}

		public Vector4(double x2, double y2, double x3, double y3) {
			// TODO Auto-generated constructor stub
			x = x2;
			y = y2;
			a = x3;
			b = y3;
		}
		
	}

